import React, { useState, useEffect } from "react";
import { Container } from './style';
import api from '../../services/api';

interface IProduct {
    id: number;
    photo: string;
    name: string;
    description: string;
    price: number;
}


const Home: React.FC = () => {
    const [data, setData] = useState<IProduct[]>([]);
    useEffect(() => {
        api.get("").then(
            response => {
                setData(response.data)
            }
        )
    }, [])

    const handleCart = ( index: number ) => {
        const productStore = JSON.stringify( data[index]);
        localStorage.setItem(`@Produto-${index}`, productStore)
    }

    return (
        <Container>

        <nav className="menus-n1" id="menus-n1">
            <a className="menu-logo" href="/"><img src="Logo.png"/></a>

            <div className="container-menu">
                <div className="menu-div-n1" id="menu-div-n1">
                    <ul className="menu-ul">
                        <li className="menu-li">
                            <a className="menu-link" href="#">Loja</a>
                        </li>
                        <li className="menu-li">
                            <a className="menu-link" href="#">Promoções</a>
                        </li>
                        <li className="menu-li">
                            <a className="menu-link" href="#">Quem somos?</a>
                        </li>
                        <li className="menu-li">
                            <a className="menu-link" href="#">Contate-nos</a>
                        </li>
                        <li className="menu-li">
                            <a className="menu-link" href="#">Trabalhe aqui</a>
                        </li>
                    </ul>
        
                    <div className="right-menu">
                        <a href="#" className="menu-link-right">EN</a>
                        <button className="btn btn-light btn-grande">Contact Us <i className="fas fa-long-arrow-alt-right"></i></button>
                    </div>
                </div>
            </div>
        </nav>




            <section>
                {data.map(( prod, index) => (
                    <div className="product-content" key={prod.id}>
                        <img src={prod.photo} alt="iphone" width="200" height="auto" />
                        <h4>{prod.name}</h4>
                        <span>{prod.description}</span>
                        <h6>{prod.price}</h6>
                        <button onClick={() => handleCart(index)}>ADICIONAR AO CARRINHO</button>
                    </div>
                ))}

            </section>
        </Container>
    );
}

export default Home;
